import React, { useState, useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import validator from "../../../utils/validator";

const FormComponent = ({
  children,
  validatorConfig,
  onSubmit,
  defaultData,
}) => {
  const [data, setData] = useState(defaultData || {});
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (Object.keys(data).length > 0) {
      validate(data);
    }
  }, [data]);

  const isValid = Object.keys(errors).length === 0;

  const validate = useCallback(
    (data) => {
      const errors = validator(data, validatorConfig);
      setErrors(errors);
      return Object.keys(errors).length === 0;
    },
    [validatorConfig, setErrors]
  );

  const handleChange = useCallback(
    (target) => {
      setData((prevState) => ({
        ...prevState,
        [target.name]: target.value,
      }));
    },
    [setData]
  );

  const handleKeyDown = useCallback((event) => {
    if (event.keyCode === 13) {
      event.preventDefault();
      const form = event.target.form;
      const indexField = Array.prototype.indexOf.call(form, event.target);
      form.elements[indexField + 1].focus();
    }
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validate(data);
    if (!isValid) return;
    onSubmit(data);
  };

  const clonedElements = React.Children.map(children, (child) => {
    const childType = typeof child.type;
    let config = {};
    if (childType === "object") {
      if (!child.props.name)
        throw new Error("Name property is requared for field component", child);
      config = {
        ...child.props,
        onChange: handleChange,
        value: data[child.props.name] || "",
        error: errors[child.props.name],
        onKeyDown: handleKeyDown,
      };
    }
    if (childType === "string") {
      if (child.type === "button") {
        if (child.props.type === "submit" || child.props.type === undefined) {
          config = { ...child.props, disabled: !isValid };
        }
      }
    }
    return React.cloneElement(child, config);
  });
  return <form onSubmit={handleSubmit}>{clonedElements}</form>;
};

FormComponent.propTypes = {
  children: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node,
  ]),
  defaultData: PropTypes.object,
  onSubmit: PropTypes.func,
  validatorConfig: PropTypes.object,
};

export default FormComponent;
