import React, { useRef } from "react";
import CollapseWrapper from "../common/collapse";

const UseRefExercise = () => {
  const blockRef = useRef();

  const handleClick = () => {
    blockRef.current.style.width = "80px";
    blockRef.current.style.height = "150px";
  };

  return (
    <CollapseWrapper title="Упражнение">
      <p className="mt-3">
        У вас есть блок, у которого заданы ширина и высота. Добавьте кнопку, при
        нажатии которой изменятся следующие свойства:
      </p>
      <ul>
        <li>Изменится содержимое блока на &quot;text&quot;</li>
        <li>высота и ширина станут равны 150 и 80 соответственно</li>
      </ul>
      <div
        className="bg-primary d-flex flex-row justify-content-center align-items-center rounded"
        style={{
          height: 40,
          width: 60,
          color: "white",
        }}
        ref={blockRef}
      >
        <small>Блок</small>
      </div>
      <button className="d-block btn btn-secondary" onClick={handleClick}>
        Изменить
      </button>
    </CollapseWrapper>
  );
};

export default UseRefExercise;
